const Title = styled.div`
  font-size: 1rem;
  margin-left: 0.5rem;
  font-weight: 600;
  margin-top: 0.5rem;
`;
const DesCenter = styled.div``;

interface Iprops {
  item: {
    productId: Number;
    productTitle: string;
    productPrice: Number;
    productThumbnailUrl: string;
    productFavorite: Number;
    productRegDt: Object;
    productCode: Number;
    productFavoriteUser: {
      authId: Number;
      userAddress: string;
      userDescription: string;
      userEmail: string;
      userEmailConfirm: boolean;
      userId: number;
      userImgUrl: string;
      userNick: string;
      userRole: string;
    }[];
  };
}

const ItemCard2: React.FC<Iprops> = ({ item }) => {
  const [liked, setLiked] = useState(false); // ÎÇ¥Í∞Ä Ï¢ãÏïÑÏöî ÌñàÎÇò
  const [MyAddress, setMyAddress] = useState(sessionStorage.getItem("userId"));
  useEffect(() => {
    var tmp = false;
    item.productFavoriteUser.map((user) => {
      if (user.userId === Number(MyAddress)) {
        tmp = true;
      }
    });
    setLiked(tmp);
  }, []);

  const navigate = useNavigate();
  const goDetailPage = () => {
    navigate(`/store/detail/${item.productId}`);
    sessionStorage.setItem("item", JSON.stringify(item));
  };
  const [likes, setLikes] = useState(item.productFavoriteUser.length);
  const LikeIt = useMutation<any, Error>( // Ï¢ãÏïÑÏöî api
    "postProductLike",
    async () => {
      return await postProductLike(Number(item.productId));
    },
    
  );
  const cancelLikeIt = useMutation<any, Error>( //Ï¢ãÏïÑÏöî Ï∑®ÏÜå api
    "delProductLike",
    async () => {
      return await delProductLike(Number(item.productId));
    },
    
  );
  const Like = () => {
    //Ï¢ãÏïÑÏöî Î≤ÑÌäº
    setLikes(likes + 1);
    LikeIt.mutate();
  };
  const cancelLike = () => {
    // Ï¢ãÏ∑® Î≤ÑÌäº
    setLikes(likes - 1);
    cancelLikeIt.mutate();
  };
  const imageOnErrorHandler = (
    // ÏÇ¨ÏßÑÏù¥ Ïò§Î•òÎÇ† Ïãú Í∏∞Î≥∏ ÏÇ¨ÏßÑ
    event: React.SyntheticEvent<HTMLImageElement, Event>
  ) => {
    event.currentTarget.src =
      "data:image/png;base64,";
  };
  return (
    <Wrapper>
      <CardWrapper id={item.productCode === 7 ? "character" : "normal"}>
        <Image
          onClick={() => {
            goDetailPage();
          }}
        >
          <img
            alt="pic"
            src={item.productThumbnailUrl}
            onError={imageOnErrorHandler}
          />
        </Image>
        <CardCenter>
          <DesLeft>
            <Title>
              {item.productTitle.length > 10 ? (
                <>{item.productTitle.substring(0, 10)}...</>
              ) : (
                <>{item.productTitle}</>
              )}
            </Title>
          </DesLeft>
          <DesCenter></DesCenter>
          <DesRight>
            {item.productPrice > 0 ? (
              <div className="dia">
                {/* <img alt="üíé" style={{ height: "1.2rem" }} src={ether} /> */}
                <div> {item.productPrice}NCT</div>
              </div>
            ) : (
              <div className="empty" />
            )}
            <div className="like">
              <div
                onClick={() => {
                  setLiked(!liked);
                }}
                className="icon"
              >
                {liked ? (
                  <FavoriteIcon
                    onClick={() => {
                      cancelLike();
                    }}
                    color="error"
                  />
                ) : (
                  <FavoriteBorderIcon
                    onClick={() => {
                      Like();
                    }}
                    color="error"
                  />
                )}
              </div>
              <p>{likes}</p>
            </div>
          </DesRight>
        </CardCenter>
      </CardWrapper>
    </Wrapper>
  );
};

export default ItemCard2;
