interface Iprops {
  user:{
    userFollowerCnt: number;
    userImgUrl: string;
    userId: number;
    userNick:string;
  }
}

const RankCitizen:React.FC<Iprops>= ({user}) => {  
  const [imgurl,setImgurl] = useState('')
  const imageOnErrorHandler = ( // 사진이 오류날 시 기본 사진
    event: React.SyntheticEvent<HTMLImageElement, Event>
  ) => {
    event.currentTarget.src ='data:image/png;base64,';
  };
  const navigate = useNavigate()
  useEffect(()=>{
    if(user!==undefined){
    if(user.userImgUrl===null){
      setImgurl('data:image/png;base64,')
    }
    else{setImgurl(user.userImgUrl)}}
  },[user])
  return (
    <RankCardWrapper>
      <RankCardDiv>
          {user!==undefined &&
        <TitleCardDiv>
          <div className="container1">
            <img
              onError={imageOnErrorHandler}
              id="profileImg"
              src={imgurl}
              alt="사진"
            />
          </div>
          <div className="container2">
            <div className="nickname">
              <h1>{user.userNick}</h1>
            </div>
            <div className="status">
              <div id="set">팔로워</div>
              <div id="data">{user.userFollowerCnt}</div>
            </div>
          </div>
        </TitleCardDiv>
        }
        <TodayInformationDiv onClick={()=>{navigate(`/mypage/${user.userId}`)}}>
          <div className="gobutton">
            {user.userNick} 프로필로 이동하기
          </div>
        </TodayInformationDiv>
      </RankCardDiv>
    </RankCardWrapper>
  );
};

export default RankCitizen;
